import React from 'react';
import logo from './logo.svg';
import './App.css';
import './stylesheet.css'


const Player = ({app}) => {
  let me = app.state.positions.me
  return (
  <div class="player">
  (<div class="player-circle" style={{top: me.top+"px", left: me.left+"px"}}>
  <div className="player-thing"></div>
  </div>
  </div>)
}

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state={
      pressedKeys: [],
      update: 0,
      positions: {
        me: {
          left: 500,
          top: 500
        }
      }
    }
  }
  handleKeyPress(e) {
    if(!this.state.pressedKeys.includes(e.key)) {
      let arrProto = this.state.pressedKeys
      arrProto.push(e.key)
      this.setState({
        pressedKeys: arrProto
      })
    }
  }
  handleKeyRelease(e) {
    console.log("release")
    let arrProto=this.state.pressedKeys;
    let newArr = arrProto.filter(key => key!==e.key)
    console.log(newArr)
    this.setState({
      pressedKeys: newArr
    })
  }
  componentDidMount() {
    let map = {
      "w": ["top", -30],
      "a": ["left", -30],
      "s": ["top", 30],
      "d": ["left", 30]
    }
    window.setInterval(() => {
      this.state.pressedKeys.forEach(key => {
        if (Object.keys(map).includes(key) && this.state.positions.me[map[key][0]]+map[key][1]>=-20) {
          let oldMe=this.state.positions.me
          oldMe[map[key][0]]+=map[key][1]
          let oldPositions=this.state.positions
          oldPositions["me"]=oldMe
          this.setState({
            positions: oldPositions
          })
        }
      });
    }, 50)
  }
  render() {
    window.onkeyup = (e) => this.handleKeyRelease(e)
    window.onkeydown=(e) => this.handleKeyPress(e)
    return (
      <div className="App">
      <Player app={this}/>
      </div>
    )
  }
}

export default App;
