import React from 'react';
import logo from './logo.svg';
import './App.css';


class App extends React.Component {
  constructor(props) {
    super(props)
    this.state={
      update: 0,
      positions: {
        me: {
          left: 500,
          top: 500
        }
      }
    }
  }
  handleKeyPress(e) {
    let map = {
      "w": ["top", -1],
      "a": ["left", -1],
      "s": ["top", 1],
      "d": ["left", 1]
    }
    console.log(e.key)
    if (Object.keys(map).includes(e.target.key)) {
      console.log(e.target.key)
    }
  }
  render() {
    window.onkeydown=(e) => this.handleKeyPress(e)
    return (
      <div className="App">
      <h1>TEST</h1>
      </div>
    )
  }
}

export default App;
