import React from 'react';
import logo from './logo.svg';
import './App.css';
import './stylesheet.css'

const Player = ({app}) => {
  let me = app.state.positions.me
  return (
    <div class="player" id="player" style={{top: me.top+"px", left: me.left+"px", transform: `rotateZ(${me.rotation}deg)`}}>
  <div className="pipe">
  <div class="shot" id="shot"></div>
  </div>
  <div class="player-shoulders">
  <div class="player-head"></div>
  </div>
  <div class="player-hands">
  <div class="player-hand right-hand"></div>
  <div class="player-hand left-hand"></div>
  </div>
  </div>)
}

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state={
      mouseX:0,
      mouseY:0,
      pressedKeys: [],
      update: 0,
      shots: [],
      positions: {
        me: {
          left: 500,
          top: 500,
          rotation: 30
        }
      }
    }
  }
  handleKeyPress(e) {
    if(!this.state.pressedKeys.includes(e.key)) {
      let arrProto = this.state.pressedKeys
      arrProto.push(e.key)
      this.setState({
        pressedKeys: arrProto
      })
    }
  }
  handleKeyRelease(e) {
    console.log("release")
    let arrProto=this.state.pressedKeys;
    let newArr = arrProto.filter(key => key!==e.key)
    console.log(newArr)
    this.setState({
      pressedKeys: newArr
    })
  }
  handleMouseMove(e) {
    this.setState({
      mouseX: e.clientX,
      mouseY: e.clientY
    })
  }
  handleClick() {
    let shots = this.state.shots
    letStartingPoint = document.getElementById("shot").getBoundingClientRect();
    shots.push({

    })
  }
  componentDidMount() {
    let map = {
      "w": ["top", -5],
      "a": ["left", -5],
      "s": ["top", 5],
      "d": ["left", 5]
    }

    let elem = document.getElementById("player")
    let elemWidth = getComputedStyle(elem).width

    window.setInterval(() => {
      let x = this.state.mouseX-this.state.positions.me.left-0.5*parseInt(elemWidth);
      let y = this.state.mouseY-this.state.positions.me.top-0.5*parseInt(elemWidth)
      let rotation = (Math.atan2(y, x)/Math.PI)*180+90
      let oldMe=this.state.positions.me
      oldMe["rotation"]=rotation
      let oldPositions=this.state.positions
      oldPositions["me"]=oldMe

      this.state.pressedKeys.forEach(key => {

        if (Object.keys(map).includes(key) && this.state.positions.me[map[key][0]]+map[key][1]>=-20) {
          oldMe[map[key][0]]+=map[key][1]
        }

        this.setState({
          positions: oldPositions
        })

      });
    }, 16)
  }
  render() {
    window.onkeyup = (e) => this.handleKeyRelease(e)
    window.onkeydown = (e) => this.handleKeyPress(e)
    window.onmousemove= (e) => this.handleMouseMove(e)
    window.onclick=this.handleClick
    return (
      <div className="App">
      <Player app={this}/>
      </div>
    )
  }
}

export default App;
