import React from 'react';
import logo from './logo.svg';
import './App.css';
import './stylesheet.css'


const Player = ({app}) => {
  let me = app.state.positions.me
  return (<div class="player" style={{top: me.top+"px", left: me.left+"px"}}>

  </div>)
}


window.onkeypress = (e) => {
  console.log(e.shiftKey)
  console.log(e.ctrlKey)
}
class App extends React.Component {
  constructor(props) {
    super(props)
    this.state={
      update: 0,
      positions: {
        me: {
          left: 500,
          top: 500
        }
      }
    }
  }
  handleKeyPress(e) {
    let map = {
      "w": ["top", -10],
      "a": ["left", -10],
      "s": ["top", 10],
      "d": ["left", 10]
    }
    if (Object.keys(map).includes(e.key)) {
      let oldMe=this.state.positions.me
      oldMe[map[e.key][0]]+=map[e.key][1]
      let oldPositions=this.state.positions
      oldPositions["me"]=oldMe
      this.setState({
        positions: oldPositions
      })
    }
  }
  render() {
    window.onkeydown=(e) => this.handleKeyPress(e)
    return (
      <div className="App">
      <h1>TEST</h1>
      <Player app={this}/>
      </div>
    )
  }
}

export default App;
